$("#express_shipping_zip").keyup(function(){$(this).parsley("validate")&&$.post("/acenda/bones/store/api/taxdata/calculate",{subtotal:$(".subtotal .amount .price").text(),shipping_rate:$(".shipping .amount .price").text(),shipping_zip:$(this).val()},"json").done(function(t){$(".tax .amount .price").text(t.result.tax);var a=parseFloat($(".subtotal .amount .price").text())+parseFloat($(".shipping .amount .price").text())+parseFloat(t.result.tax);$(".total .amount .price").text(a.toFixed(2))})}),$("#express_shipping_address_id").change(function(){var t={subtotal:$(".subtotal .amount .price").text(),shipping_rate:$(".shipping .amount .price").text()};""==$(this).val()?t.shipping_zip=$("#express_shipping_zip").val():t.shipping_address_id=$("#express_shipping_address_id").val(),$.post("/acenda/bones/store/api/taxdata/calculate",t,"json").done(function(t){$(".tax .amount .price").text(t.result.tax);var a=parseFloat($(".subtotal .amount .price").text())+parseFloat($(".shipping .amount .price").text())+parseFloat(t.result.tax);$(".total .amount .price").text(a.toFixed(2))})});