showProductVariation=function(n){var t=n.parentsUntil(".product").parent(),i=t.parentsUntil(".variations").parent(),a=n.prop("selectedIndex"),e=n.val(),c=t.find("input").val();i.children(".product").each(function(){$(this).hide(),$(this).find("input").each(function(){$(this).val(0)}),$(this).find("select").each(function(){$(this).val(e)})}),$("#"+e).show(),$("#"+e).find("input").val(c)};